export default {
  name: 'Warrants',
  layout: 'master',
  data() {
    return {
      usersList: [],
      selectedEmployee: [],
      showEmptyEmployeesMessage: false,
      showEmptyChatMessage: false,
      selectedUserId: ''
    }
  },
  mounted() {
    this.fetchAllUsers()

    this.socket = this.$nuxtSocket({
      channel: ''
    })

    this.socket.emit('connection', {
        hello: 'world' 
      }, (resp) => {
        /* Handle response, if any */
      })


    /* Listen for events: */
    this.socket
    .on('pong', (msg, cb) => {
      /* Handle event */
      alert('pong')
      console.log(msg, cb)
    })
  },

  computed: {
    selectedEmployeeComputed () {
      return this.selectedEmployee
    }
  },

  methods: {
    emit () {
      alert('going to ping')
      this.socket.emit('ping', {
        hello: 'world' 
      }, (resp) => {
        /* Handle response, if any */
      })
    },
    async fetchAllUsers(){
      this.selectedUserId = this.$route.query.user_id
      const users = this.usersList = await this.$store.dispatch(
        'auth/fetchAllUsers',
        {}
      )
      if(users.length > 0) {
        users.forEach(user => {
          if(user.enc_user_id == this.selectedUserId) {
            alert('yes')
            this.selectedEmployee = user
          }
        });
      } else {
        this.showEmptyMessage = true
      }

    },
  }
}
</script>



