</template>

<script>
import { mapGetters } from 'vuex'
import deleteReportModal from '~/components/police-reports/deleteReportModal.vue'

export default {
  components: { deleteReportModal },

  name: 'Reports',
  layout: 'master',
  middleware: ['reportsMW'],
  data() {
    return {
      policeReports: [],
      policeReportsFiltered: [],
      report_search: '',
    }
  },
  watch: {
      report_search(newValue, oldValue){
          if(newValue.length > 2){
              this.searchReports(newValue)
          }else {
            this.policeReportsFiltered = this.policeReports

          }
      }
  },
  computed: {
    policeReportsFilteredComputed() {
      return this.policeReportsFiltered
    },
    ...mapGetters({
      isAllowedToDeletePoliceReports: 'auth/isAllowedToDeletePoliceReports',
      isAllowedToDeleteMedicalReports: 'auth/isAllowedToDeleteMedicalReports',
      isAllowedToEditPoliceReports: 'auth/isAllowedToEditPoliceReports',
      isAllowedToEditMedicalReports: 'auth/isAllowedToEditMedicalReports',
    }),
  },
  mounted() {
    this.fetchAllReports()
    this.$store.commit('setActiveTab', 'search')
  },

  methods: {
    showDeleteReportModal (reportId,ReportType) {
      this.$store.commit('report/setIsDeleteReportPopupOpen', true)
      this.$store.commit('report/setDeleteReportId', reportId)
      this.$store.commit('report/setDeleteReportType', ReportType)

    },
    editReport (reportId,ReportType) {
      if(ReportType === "police_report") {
        this.$router.push({ path: 'create-police-report', query: { report_id: reportId } })
      }else{
        this.$router.push({ path: 'create-medical-report', query: { report_id: reportId } })

      }

    },
    openReportModalPopUp (report) {
      if(report.report_type === 'police_report') {
        this.fetchPoliceReportByReportId(report.report_id)
        this.$store.commit('report/setIsPoliceReportPopupOpen', true)
      } else {
        this.fetchMedicalReportByReportId(report.report_id)
        this.$store.commit('report/setIsMedicalReportPopupOpen', true)
      }
    },
    async fetchPoliceReportByReportId (reportId) {
      await this.$store.dispatch(
        'report/fetchPoliceReportByReportId',
        {
          report_id : reportId
        }
      )
    },
    async fetchMedicalReportByReportId (reportId) {
      await this.$store.dispatch(
        'report/fetchMedicalReportByReportId',
        {
          report_id : reportId
        }
      )
    },
    async fetchAllReports () {
      this.policeReports = await this.$store.dispatch(
        'report/fetchAllReports',
        {}
      )
      this.policeReportsFiltered = this.policeReports
    },
    searchReports(searchedName) {
        searchedName = searchedName.toLowerCase()
        var list = this.policeReports

        const filtered = list.filter( (
              report,
              index,
              array
          ) => {
              let name = report.report_title.toLowerCase()
              if (name.includes(searchedName)) {
              return true
              } else {
              return false
              }
          })
          this.policeReportsFiltered = filtered
    }
  },
}
</script>
